/*
 * 1. traverse the matrix, and mark first island to 2 with dfs, push each point of second island to queue.
 * 2. bfs the queue to try to reach first island, and return shortest distance.
 */

#include <queue>
#include <vector>
#include <iostream>
using namespace std;

class Solution {
public:
    // MLE, wtf... but java version is ok...
    int shortestBridge(vector<vector<int>>& A) {
        int m = A.size(), n = A.front().size();
        queue<pair<int, int>> q;

        // dfs
        bool findFirst = false;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (A[i][j] == 1 && !findFirst) {
                    dfs(A, i, j, q);
                    findFirst = true;
                    break;
                }
                if (A[i][j] == 1 && findFirst) {
                    q.push({i, j});                         // push as second island
                }
            }
        }

        // bfs
        int distance = 0;
        while (!q.empty()) {
            size_t size = q.size();
            for (size_t i = 0; i < size; i++) {
                int x = q.front().first, y = q.front().second;
                q.pop();
                for (auto& dir : dirs) {
                    int next_x = x + dir.first;
                    int next_y = y + dir.second;
                    
                    if (!isValid(A, next_x, next_y)) {
                        continue;
                    }

                    if (A[next_x][next_y] == 2) {
                        return distance;
                    } else if (A[next_x][next_y] == 1) {
                        continue;
                    } else if (A[next_x][next_y] == 0) {
                        A[x][y] = 1;                        // mark as second island
                        q.push({next_x, next_y});
                    }
                }
            }
            distance++;
        }
        return -1;
    }

private:
    void dfs(vector<vector<int>>& A, int x, int y, queue<pair<int, int>> &q) {
        A[x][y] = 2;                                        // mark as first island
        for (auto& dir : dirs) {
            int next_x = x + dir.first;
            int next_y = y + dir.second;
            if (isValid(A, next_x, next_y) && A[next_x][next_y] == 1) {
                dfs(A, next_x, next_y, q);
            }
        }
    }

    bool isValid(vector<vector<int>>& A, int x, int y) {
        if (x < 0 || x >= A.size() || y < 0 || y >= A.front().size()) {
            return false;
        }
        return true;
    }

    vector<pair<int, int>> dirs{{0, 1}, {1, 0}, {0, -1}, {-1, 0}};

    void display(vector<vector<int>>& A) {
        for (int i = 0; i < A.size(); i++) {
            for (int j = 0; j < A.front().size(); j++) {
                cout << A[i][j] << " ";
            }
            cout << endl;
        }
        cout << endl;
    }
};

